SELECT a.*, c.cl_name charge_wh_name FROM (SELECT i.wh_code, p.pay_no, p.pay_ymd, p.content, p.pay_chg, p.sts_code FROM payment p, in_order i WHERE p.pay_no = i.pay_no and p.sts_code = 'PY000' or p.sts_code = 'PY001' and i.co_code = #{co_code}
and i.wh_code = #{keyword}
ORDER BY p.pay_no DESC) a, client c WHERE a.wh_code = c.cl_code

SELECT COUNT(*) FROM (SELECT i.wh_code, p.pay_no, p.pay_ymd, p.content, p.pay_chg, p.sts_code FROM payment p, in_order i WHERE p.pay_no = i.pay_no and p.sts_code = 'PY000' or p.sts_code = 'PY001' and i.co_code = #{co_code}
and i.wh_code = #{keyword}
ORDER BY p.pay_no DESC) a, client c WHERE a.wh_code = c.cl_code

